" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Radiology of the trachea",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?24/42/25256/contributors\">",
