var title_f24_43_25276="Treatment of Chagas disease";
var content_f24_43_25276=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F65745&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F65745&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Treatment of Chagas disease",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Treatment of Chagas disease by phase of disease, form of disease, and demographic group",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Should always be offered",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Acute",
"        <em>",
"         T. cruzi",
"        </em>",
"        infection",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Early congenital",
"        <em>",
"         T. cruzi",
"        </em>",
"        infection",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Children &le;12 years old with chronic",
"        <em>",
"         T. cruzi",
"        </em>",
"        infection",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Children 13-18 years old with chronic",
"        <em>",
"         T. cruzi",
"        </em>",
"        infection",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Reactivated",
"        <em>",
"         T. cruzi",
"        </em>",
"        infection in patient with HIV/AIDS or other immunosuppression",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Should generally be offered",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Reproductive-age women",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Adults 19-50 years old with indeterminate form, or mild to moderate cardiomyopathy (Kuschnir grades 0, I or II)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Optional",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Adults &gt;50 years old without advanced cardiomyopathy (Kuschnir grades 0, I or II)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Chagas gastrointestinal disease patients without advanced cardiomyopathy*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Should generally not be offered",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Advanced chagasic cardiomyopathy with congestive heart failure (Kuschnir grade III)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Megaesophagus with significant impairment of swallowing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Should never be offered",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        During pregnancy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Severe renal or hepatic insufficiency",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * There are no data to suggest that treatment affects progression of gastrointestinal disease. Decisions should be based on the potential to decrease risk of development or progression of heart disease.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Bern C, Montgomery SP, Herwaldt BL, et al. Evaluation and treatment of chagas disease in the United States: a systematic review. JAMA 2007; 298:2171. Copyright &copy; 2007 American Medical Association. All rights reserved.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
