var title_f24_44_25292="Wrist examination";
var content_f24_44_25292=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F51902&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F51902&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Examination of the wrist",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Examination signs",
"       </td>",
"       <td class=\"subtitle1\">",
"        Diagnosis",
"       </td>",
"       <td class=\"subtitle1\">",
"        Confirmation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pain and stiffness at full wrist flexion or extension or both*",
"       </td>",
"       <td rowspan=\"3\">",
"        #1 - Wrist sprain",
"       </td>",
"       <td rowspan=\"3\">",
"        <p>",
"         Wrist series x-ray is normal",
"        </p>",
"        <p>",
"         Resolves in two weeks",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Minimal dorsal swelling",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Minimal tenderness over the navicular and lunate bones",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Loss of full wrist flexion and full wrist extension*",
"       </td>",
"       <td rowspan=\"2\">",
"        Radiocarpal arthritis",
"       </td>",
"       <td rowspan=\"2\">",
"        Wrist series x-ray or local anesthetic block in the radiocarpal joint",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Dorsal swelling and tenderness",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Loss of full wrist flexion and full wrist extension*",
"       </td>",
"       <td rowspan=\"3\">",
"        Navicular fracture",
"       </td>",
"       <td rowspan=\"3\">",
"        Wrist series x-ray or bone scan",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Dorsal swelling and tenderness",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Point tenderness over the navicular or lunate dorsally or the snuff box",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Cystic swelling over the dorsum of the wrist*",
"       </td>",
"       <td>",
"        Dorsal ganglion",
"       </td>",
"       <td>",
"        Simple aspiration of gelatinous fluid",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Dysesthesias or paresthesias by history*",
"       </td>",
"       <td rowspan=\"2\">",
"        Carpal tunnel syndrome",
"       </td>",
"       <td rowspan=\"2\">",
"        Medial nerve block with anesthesia or nerve conduction studies",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tinel or Phalen sign",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Point tenderness over the lateral wrist just distal to the ulnar styloid*",
"       </td>",
"       <td rowspan=\"2\">",
"        Triangular cartilage injury",
"       </td>",
"       <td rowspan=\"2\">",
"        MRI of the wrist or anesthetic block in the ulnar-radial joint",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pain with forced ulnar deviation",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Pain with resisted finger extension*",
"       </td>",
"       <td rowspan=\"2\">",
"        Dorsal tenosynovitis",
"       </td>",
"       <td rowspan=\"2\">",
"        Anesthetic block of the tenosynovial sheath",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Diffuse swelling over the dorsum",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     *: Is used to identify the hallmark sign; that is, the sign that has the greatest specificity for the diagnosis listed.",
"     <br>",
"      #1: Refers to the most common condition affecting the wrist.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
