var content_f24_44_25293=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F58913&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F58913&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Suggested intravenous antimicrobial treatment regimens for bacterial tracheitis in children",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Drug",
"      </td>",
"      <td class=\"subtitle1\">",
"       Dose",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\">",
"       For patients without hypersensitivity to penicillin or cephalosporin",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" rowspan=\"2\">",
"       <p>",
"        Vancomycin*&bull;",
"       </p>",
"       <p>",
"        <strong>",
"         OR",
"        </strong>",
"       </p>",
"      </td>",
"      <td>",
"       40 to 60 mg/kg per day in three to four divided doses",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Maximum daily dose 4 g",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" rowspan=\"2\">",
"       Clindamycin",
"      </td>",
"      <td>",
"       40 mg/kg per day in three divided doses",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Maximum daily dose 2.7 g",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" colspan=\"2\">",
"       <strong>",
"        <span class=\"red\">",
"         PLUS",
"        </span>",
"       </strong>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" rowspan=\"2\">",
"       <p>",
"        Ceftriaxone&Delta;",
"       </p>",
"       <p>",
"        <strong>",
"         OR",
"        </strong>",
"       </p>",
"      </td>",
"      <td>",
"       50 to 100 mg/kg per day once daily or in two divided doses",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Maximum daily dose 2 g",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" rowspan=\"2\">",
"       <p>",
"        Cefotaxime&bull;&Delta;",
"       </p>",
"       <p>",
"        <strong>",
"         OR",
"        </strong>",
"       </p>",
"      </td>",
"      <td>",
"       150 to 200 mg/kg per day in four divided doses",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Maximum daily dose 10 g",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" rowspan=\"2\">",
"       Ampicillin-sulbactam&bull;",
"      </td>",
"      <td>",
"       150 to 200 mg/kg (of ampicillin component) per day in four divided doses",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Maximum daily dose 8 g",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\">",
"       For patients with potential severe hypersensitivity to beta-lactam antibiotics (eg, penicillin, cephalosporin)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" rowspan=\"2\">",
"       <p>",
"        Vancomycin*&bull;",
"       </p>",
"       <p>",
"        <strong>",
"         OR",
"        </strong>",
"       </p>",
"      </td>",
"      <td>",
"       40 to 60 mg/kg per day in three to four divided doses",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Maximum daily dose 4 g",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" rowspan=\"2\">",
"       Clindamycin",
"      </td>",
"      <td>",
"       40 mg/kg per day in three divided doses",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Maximum daily dose 2.7 g",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" colspan=\"2\">",
"       <strong>",
"        <span class=\"red\">",
"         PLUS",
"        </span>",
"       </strong>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" rowspan=\"2\">",
"       <p>",
"        Levofloxacin&bull;",
"       </p>",
"       <p>",
"        <strong>",
"         OR",
"        </strong>",
"       </p>",
"      </td>",
"      <td>",
"       <p>",
"        6 months to 5 years: 20 mg/kg per day in two divided doses",
"       </p>",
"       <p>",
"        &ge;5 years: 10 mg/kg once per day",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Maximum daily dose 500 mg",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" rowspan=\"2\">",
"       Ciprofloxacin&bull;",
"      </td>",
"      <td>",
"       20 to 30 mg/kg per day in two divided doses",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Maximum daily dose 800 mg",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\">",
"       For patients with nonanaphylactic hypersensitivity to penicillin and cephalosporin",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" rowspan=\"2\">",
"       <p>",
"        Vancomycin*&bull;",
"       </p>",
"       <p>",
"        <strong>",
"         OR",
"        </strong>",
"       </p>",
"      </td>",
"      <td>",
"       40 to 60 mg/kg per day in three to four divided doses",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Maximum daily dose 2 g",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" rowspan=\"2\">",
"       Clindamycin",
"      </td>",
"      <td>",
"       40 mg/kg per day in three divided doses",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Maximum daily dose 2.7 g",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" colspan=\"2\">",
"       <strong>",
"        <span class=\"red\">",
"         PLUS",
"        </span>",
"       </strong>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" rowspan=\"2\">",
"       <p>",
"        Aztreonam&bull;",
"       </p>",
"       <p>",
"        <strong>",
"         OR",
"        </strong>",
"       </p>",
"      </td>",
"      <td>",
"       90 to 120 mg/kg per day in three to four divided doses",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Maximum daily dose 8 g",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" rowspan=\"2\">",
"       Meropenem&bull;",
"      </td>",
"      <td>",
"       60 to 120 mg/kg per day in three divided doses",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Maximum daily dose 6 g",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Vancomycin is indicated in patients with moderate to severe sepsis, patients who may have concomitant meningitis, and patients from areas with an increased prevalence of clindamycin-resistant and/or methicillin-resistant Staphylococcus aureus isolates. Trough levels of 15-20 mcg/mL are suggested for severe infections.",
"     <br>",
"      &bull; Dosage modification for renal insufficiency is necessary. See drug information topic.",
"      <br>",
"       &Delta; A third-generation cephalosporin (ceftriaxone or cefotaxime) is preferred to ampicillin sulbactam.",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
