var title_f24_46_25324="NCCN criteria for palliative care assessment";
var content_f24_46_25324=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PALC%2F87162&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PALC%2F87162&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    NCCN criteria for palliative care assessment",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"2\" width=\"50%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Patient characteristics",
"       </td>",
"       <td class=\"subtitle1\">",
"        Social circumstances or issues related to anticipatory bereavement",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Limited options for treatment",
"       </td>",
"       <td>",
"        Limited access to care",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        High risk of poor pain control or refractory pain. Risk factors include:",
"        <ul>",
"         <li>",
"          Neuropathic pain",
"         </li>",
"         <li>",
"          Incident or breakthrough pain",
"         </li>",
"         <li>",
"          Rapid escalation of opioid dose",
"         </li>",
"         <li>",
"          History of substance (drug, alcohol) abuse",
"         </li>",
"         <li>",
"          Impaired cognition",
"         </li>",
"         <li>",
"          Psychosocial/family distress",
"         </li>",
"        </ul>",
"       </td>",
"       <td>",
"        Familial factors, including:",
"        <ul>",
"         <li>",
"          Limitations of the family/caregiver",
"         </li>",
"         <li>",
"          Inadequate family support",
"         </li>",
"         <li>",
"          Family discord",
"         </li>",
"         <li>",
"          History of intensely dependent relationship(s)",
"         </li>",
"         <li>",
"          Parental concerns regarding care of dependents",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        High symptom burden or distress score",
"       </td>",
"       <td>",
"        Financial limitations",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Intensive care unit admission with multiple complications or need for prolonged ventilator support",
"       </td>",
"       <td>",
"        Unresolved grief or multiple prior losses",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Physical symptoms other than pain that are refractory to conventional management",
"       </td>",
"       <td>",
"        Spiritual or existential crisis",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Cognitive impairment",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Severe or multiple comorbid conditions",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Communication barriers related to language, literacy, or physical issues",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Request for hastened death",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inability to engage in advance care planning and care plan",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Source: NCCN Guidelines Version 2.2012. Palliative Care.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
