var title_f24_48_25355="NSAID dosing in recurrent pericarditis";
var content_f24_48_25355=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F50462&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F50462&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    NSAID dosing in recurrent pericarditis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Drug",
"       </td>",
"       <td class=\"subtitle1\">",
"        Attack dose",
"       </td>",
"       <td class=\"subtitle1\">",
"        Treatment length",
"       </td>",
"       <td class=\"subtitle1\">",
"        Monitoring",
"       </td>",
"       <td class=\"subtitle1\">",
"        Tapering*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ibuprofen",
"       </td>",
"       <td>",
"        600 to 800 mg three times daily",
"       </td>",
"       <td>",
"        1 to 2 weeks*",
"       </td>",
"       <td>",
"        Blood cell count, CRP",
"       </td>",
"       <td>",
"        Decrease the dose every 1 to 2 weeks once CRP has normalized",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Indomethacin",
"       </td>",
"       <td>",
"        50 mg three times daily",
"       </td>",
"       <td>",
"        1 to 2 weeks*",
"       </td>",
"       <td>",
"        Blood cell count, CRP",
"       </td>",
"       <td>",
"        Decrease the total daily dose by 25 mg every 1 to 2 weeks once CRP has normalized",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Aspirin",
"       </td>",
"       <td>",
"        650 to 1000 mg three times daily",
"       </td>",
"       <td>",
"        1 to 2 weeks*",
"       </td>",
"       <td>",
"        Blood cell count, CRP",
"       </td>",
"       <td>",
"        Decrease the dose every 1 to 2 weeks once CRP has normalized",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     CRP: C-reactive protein.",
"     <br>",
"      * Therapy should be tailored according to symptoms, markers of inflammation elevation.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
