var title_f24_5_24667="DSM-IV-TR diagnostic criteria for cannabis abuse";
var content_f24_5_24667=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PSYCH%2F53923&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PSYCH%2F53923&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    DSM-IV-TR diagnostic criteria for cannabis abuse",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       The two criteria for cannabis abuse are:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       1. A maladaptive pattern of cannabis use leading to clinically significant impairment or distress, as manifested by one or more of the following, during a 12 month period:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       a. Recurrent cannabis use that interferes with major role obligations at work, school, or home. Examples include frequent absences, poor performance, suspensions, job loss or expulsion from school, neglect of children or household.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       b. Recurrent cannabis use in situations in which it is physically hazardous. Examples include driving or operating machinery when intoxicated.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       c. Recurrent legal problems related to cannabis use.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       d. Persistent cannabis use despite continued social or interpersonal problems caused or exacerbated by the effects of cannabis, such as arguments with a spouse about the consequences of intoxication.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       2. The symptoms have never met criteria for cannabis dependence.",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reprinted with permission from: the Diagnostic and Statistical Manual of Mental Disorders, Text Revision, Fourth Edition (Copyright 2000). American Psychiatric Association.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
