var title_f24_5_24669="Syndromic microcephaly";
var content_f24_5_24669=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F61045&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F61045&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Selected syndromes with microcephaly as a characteristic feature",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Syndrome",
"       </td>",
"       <td class=\"subtitle1\">",
"        Clinical features",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <p>",
"         Down syndrome",
"        </p>",
"        <p>",
"         OMIM #190685",
"        </p>",
"       </td>",
"       <td>",
"        Brachycephaly, upslanting palpebral fissures, epicanthal folds, short neck, transverse palmar crease, space between first and second toes, hypotonia",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Trisomy 18",
"       </td>",
"       <td>",
"        Prominent occiput, narrow bifrontal diameter, hypoplastic supraorbital ridge, short palpebral fissures, micrognathia, structural cardiac lesions (ventricular septal defect, atrial septal defect, patent ductus arteriosus)",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Trisomy 13",
"       </td>",
"       <td>",
"        Holoprosencephaly, wide saggital suture, cleft lip, cleft palate, loose skin, transverse palmar crease, polydactyly, posterior prominence of heel; structural cardiac lesions (ventricular septal defect, patent ductus arteriosus, atrial septal defect, dextrocardia)",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Fetal alcohol syndrome",
"       </td>",
"       <td>",
"        Pre- and postnatal growth retardation, short palpebral fissures, flat philtrum, thin upper lip",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <p>",
"         Seckel syndrome",
"        </p>",
"        <p>",
"         OMIM #210600",
"        </p>",
"       </td>",
"       <td>",
"        Pre- and postnatal growth retardation, average birth weight approximately 1540 g, proportionate short stature; micrognathia, facial asymmetry, downslanting palpebral fissures, prominent beaked nose; limb hypoplasia; gap between first and second toes",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <p>",
"         Smith-Lemli-Opitz",
"        </p>",
"        <p>",
"         OMIM #270400",
"        </p>",
"       </td>",
"       <td>",
"        Ptosis, broad nasal tip, anteverted nostrils, cleft palate, micrognathia, congenital heart defects, syndactyly of second and third toes, postaxial polydactyly, hypospadias or cryptorchidism (in boys)",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <p>",
"         Williams (7q11.23 deletion)",
"        </p>",
"        <p>",
"         OMIM #194050",
"        </p>",
"       </td>",
"       <td>",
"        Cardiovascular disease (supravalvular aortic stenosis), idiopathic hypercalcemia, periorbital fullness, short upturned nose, long philtrum, wide mouth, full lips",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <p>",
"         Cornelia de Lange",
"        </p>",
"        <p>",
"         OMIM 122470, 300590, 610759",
"        </p>",
"       </td>",
"       <td>",
"        Pre- and postnatal growth retardation, generalized hirsuitism, fusion of eyebrows (synophrys), arched brows, long eyelashes, small upturned nose, thin lips, midline beaking of the upper lip; limb reduction defects, missing fingers, syndactyly of second and third toes",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <p>",
"         Miller-Dieker (17p13.3 deletion)",
"        </p>",
"        <p>",
"         OMIM #247200",
"        </p>",
"       </td>",
"       <td>",
"        Bitemporal narrowing, upturned nose, small jaw, vertical furrowing of forehead, micrognathia, genitourinary anomalies",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <p>",
"         Wolf-Hirschhorn (4p deletion)",
"        </p>",
"        <p>",
"         OMIM #194190",
"        </p>",
"       </td>",
"       <td>",
"        Congenital heart disease, hearing loss, prominent glabella, hypertelorism, wide nasal bridge, beaked nose, short philtrum, down-turned upper lip",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <p>",
"         Cri-du-chat (5p15.2 deletion)",
"        </p>",
"        <p>",
"         OMIM #123450",
"        </p>",
"       </td>",
"       <td>",
"        Round face, hypertelorism, micrognathia, epicanthal folds, hypotonia, high-pitched cry",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <p>",
"         Monosomy 1p36 deletion",
"        </p>",
"        <p>",
"         OMIM #607872",
"        </p>",
"       </td>",
"       <td>",
"        Brachycephaly, large fontanelle, pointed chin, hearing loss, flat nasal bridge, flat nose, cleft lip, cleft palate, short fifth finger",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <p>",
"         Mowat-Wilson",
"        </p>",
"        <p>",
"         OMIM #235730",
"        </p>",
"       </td>",
"       <td>",
"        Pre- or postnatal microcephaly, short stature, hypertelorism, iris coloboma, deep-set eyes, downslanting palpebral fissures, cupped ears, pointed chin, seizures, hypospadias (in boys), Hirschsprung's disease, congenital heart disease",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <p>",
"         Rubinstein-Taybi",
"        </p>",
"        <p>",
"         OMIM #180849",
"        </p>",
"       </td>",
"       <td>",
"        Postnatal short stature, low anterior hairline, hypoplastic maxilla, micrognathia, heavy eyebrows, long eyelashes, broad thumbs and big toes",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <p>",
"         Pseudo-TORCH (Aicardi-Gouti&egrave;res syndrome)",
"        </p>",
"        <p>",
"         OMIM #225750",
"        </p>",
"       </td>",
"       <td>",
"        Congenital microcephaly, abnormal eye movements, hepatosplenomegaly, cerebral calcification, thrombocytopenia, spasticity, seizures",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from:",
"     <br>",
"      <ol>",
"       <li>",
"        Abuelo D. Microcephaly syndromes. Semin Pediatr Neurol 2007; 14:118.",
"       </li>",
"       <li>",
"        Firth HV, Hurst JA, Hall JG. Microcephaly. In: Oxford Desk Reference: Clinical Genetics, 1st ed. Oxford University Press, Oxford 2005. p.172.",
"       </li>",
"      </ol>",
"     </br>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
