"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Usually not needed*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Normal left ventricular function",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Coronary arteries not suitable for revascularization",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Usually needed*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Suspected CAD/IDCM, being considered for heart",
"transplantation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        CAD plus severe left ventricular dysfunction (LVEF &le;0.35)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
