var title_f24_57_25500="Etiologies of MVP";
var content_f24_57_25500=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F83655&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F83655&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Etiologies of mitral valve prolapse and mechanisms of regurgitation",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Disease",
"       </td>",
"       <td class=\"subtitle1\">",
"        Mechanism of regurgitation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Primary MVP",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Fibroelastic deficiency",
"       </td>",
"       <td>",
"        <p>",
"         Isolated prolapse of the mitral leaflet (commonly P2 scallop)",
"        </p>",
"        <p>",
"         Frequent chordal rupture",
"        </p>",
"        <p>",
"         Mild annular enlargement",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Forme fruste Barlow disease",
"       </td>",
"       <td>",
"        Intermediate",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Barlow disease",
"       </td>",
"       <td>",
"        <p>",
"         Diffusely thickened, redundant mitral leaflets",
"        </p>",
"        <p>",
"         Chordal elongation/rupture",
"        </p>",
"        <p>",
"         Severe annular enlargement",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Secondary MVP",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Associated with connective tissue disease*",
"       </td>",
"       <td>",
"        <p>",
"         Diffusely thickened, redundant mitral leaflets",
"        </p>",
"        <p>",
"         Chordal elongation/rupture",
"        </p>",
"        <p>",
"         Severe annular enlargement",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Associated with congenital heart disease",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"       <td>",
"        <p>",
"         Thickened, redundant mitral leaflets",
"        </p>",
"        <p>",
"         Chordal elongation/rupture possible",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Acute myocardial ischemia",
"       </td>",
"       <td>",
"        Papillary muscle dysfunction with secondary prolapse/papillary muscle rupture",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Acute rheumatic fever",
"       </td>",
"       <td>",
"        Chordal and leaflet destruction by acute inflammatory process",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Endocarditis",
"       </td>",
"       <td>",
"        Chordal and leaflet destruction by infectious process; vegetations",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Other (trauma, severe mitral annular calcification, hypertrophic cardiomyopathy)",
"       </td>",
"       <td>",
"        Ruptured chordae, no myxomatous changes of mitral valve leaflets",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Associated with Marfan and Ehlers-Danlos syndromes, osteogenesis imperfecta, and pseudoxanthoma elasticum. Macroscopical and microscopical appearance similar to primary Barlow disease.",
"     <br/>",
"     <span class=\"bullet\">",
"      &bull;",
"     </span>",
"     Most common congenital disease associations are Ebstein's anomaly and atrial septal defect.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
