var title_f24_6_24685="Genetic terms";
var content_f24_6_24685=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F72267&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F72267&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Genetic terms",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Term",
"      </td>",
"      <td class=\"subtitle1\">",
"       Definition",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Genotype",
"      </td>",
"      <td>",
"       The genetic blueprint",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Phenotype",
"      </td>",
"      <td>",
"       The translation of the genotype into the physical or biochemical manifestation in an individual",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Penetrance",
"      </td>",
"      <td>",
"       The frequency with which the genotype results in any phenotypic changes",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Variable expressivity",
"      </td>",
"      <td>",
"       The range of phenotypic changes (age of onset, severity of disease, different signs and symptoms, etc.) that may result from aspecific genotype",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Mutation",
"      </td>",
"      <td>",
"       A change in the sequence of DNA that results in a phenotypic change; a mutation may be beneficial, neutral or harmful",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Polymorphism",
"      </td>",
"      <td>",
"       A variation in DNA sequence that occurs in greater than 1 percent of the population",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Presymptomatic",
"      </td>",
"      <td>",
"       Term used in predictive genetic testing to identify persons at increased risk for genetic disease",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Predispositional",
"      </td>",
"      <td>",
"       Term used in predictive genetic testing; refers to detecting a genotype with less than 100 percent penetrance in an asymptomatic individual - ie, the person may or may not develop the condition",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"       Types of chromosomes",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle3_left\">",
"       Autosomal chromosomes",
"      </td>",
"      <td>",
"       22 paired sets that are not sex chromosomes",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Allele",
"      </td>",
"      <td>",
"       Each of the pair of genes on the homologous chromosomes",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Homozygous",
"      </td>",
"      <td>",
"       When both of the paired alleles have the mutation",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Heterozygous",
"      </td>",
"      <td>",
"       When the mutation occurs on only one of the allele pairs",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Compound heterozygote",
"      </td>",
"      <td>",
"       Genotype formed by each allele having a different mutation",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Sex-linked chromosome",
"      </td>",
"      <td>",
"       The X and Y chromosomes - XX in females, XY in males",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"       Modes of inheritance",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Multifactorial",
"      </td>",
"      <td>",
"       A constellation of genetic and environmental factors that interact to produce a specific condition",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle3_left\">",
"       Mendelian inheritance",
"      </td>",
"      <td>",
"       Autosomal dominant and recessive inheritance, and x-linked inheritance",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Autosomal dominant inheritance",
"      </td>",
"      <td>",
"       When a disorder can result if one of a specific pair of genes has a mutation. Child of an affected individual has a 50 percent chance of inheriting the gene involved.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Autosomal recessive inheritance",
"      </td>",
"      <td>",
"       When a disorder can result only if both genes in a specific pair have a mutation. Child of an affected individual has a 25 percent chance of inheriting the gene involved.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       X-linked inheritance",
"      </td>",
"      <td>",
"       Mutation exists on X-chromosome: Most x-linked conditions are recessive - two copies of a specific gene on the paired X chromosome must be abnormal for a female to be affected.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle3_left\">",
"       Non-Mendelian inheritance",
"      </td>",
"      <td>",
"       Pattern of inheritance that is not autosomal dominant, autosomal recessive or x-linked",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Polygenic",
"      </td>",
"      <td>",
"       When more than one gene is involved in producing a condition",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Mitochondrial",
"      </td>",
"      <td>",
"       Condition arising from a mutation in the DNA of the mitochondria, inherited from the mother only",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\">",
"       Types of genetic testing",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Diagnostic genetic testing",
"      </td>",
"      <td>",
"       Assessment DNA sequence or structure for defined genetic risk factors for the purpose of supporting a specific disease diagnosis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Carrier screening",
"      </td>",
"      <td>",
"       Assessment DNA sequence or structure for specific disease causing alleles in at risk populations",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Prenatal testing",
"      </td>",
"      <td>",
"       Assessment of fetal DNA sequence or structure for specific disease-causing alleles or karyotypes",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Preimplantation testing",
"      </td>",
"      <td>",
"       Assessment of sequence or structure of blastocyst derived DNA with the purpose of selecting embryos for implantation that are free of pathogenic genotypes",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Newborn screening",
"      </td>",
"      <td>",
"       A variety of genetic and metabolic tests performed in the newborn period for the purposes of early (ie, preclinical) identification of children with disease-causing genotypes",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
