" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Paroxysmal hemicrania: Clinical features and diagnosis",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?24/60/25545/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?24/60/25545/contributors\">",
"     Manjit S Matharu, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?24/60/25545/contributors\">",
"     Anna S Cohen, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?24/60/25545/contributors\">",
"     Section Editor",
