var title_f24_60_25548="BAL drug induced lung disease";
var content_f24_60_25548=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F52198&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F52198&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Diagnostic value of bronchoalveolar lavage in drug-induced lung disease",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Typical findings in BAL",
"       </td>",
"       <td class=\"subtitle1\">",
"        Typical findings in BAL",
"       </td>",
"       <td class=\"subtitle1\">",
"        Drug",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"3\">",
"        Cytotoxic reactions",
"       </td>",
"       <td>",
"        Atypical cells (cytomegaly, cytoplasmic eosinophilia, bizarre cell shape,&nbsp;nuclear&nbsp;hyperchromasia, prominent nucleoli, multinucleation)",
"       </td>",
"       <td rowspan=\"3\">",
"        Chemotherapeutic agents: bleomycin, methotrexate, nitrosoureas, busulfan, cyclophosphamide",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Increased eosinophils",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Extracellular lipoproteinaceous debris",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        Pulmonary hemorrhage",
"       </td>",
"       <td>",
"        Hemosiderin-laden alveolar macrophages",
"       </td>",
"       <td rowspan=\"2\">",
"        D-penicillamine, mineral oil, amphotericin B",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Red blood cells",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"3\">",
"        Lymphocytic alveolitis",
"       </td>",
"       <td>",
"        40 to 50 percent lymphocytes",
"       </td>",
"       <td rowspan=\"2\">",
"        Methotrexate, nitrofurantoin, azathioprine, sulfasalazine, cyclophosphamide, amiodarone",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Increased CD8+ cells suggestive of hypersensitivity reactions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Decreased CD4+ to CD8+ ratio",
"       </td>",
"       <td>",
"        BCG therapy, acebutolol, gold salts, propranolol, busulfan, nilutamide, bleomycin, flecainide",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Neutrophilic alveolitis",
"       </td>",
"       <td>",
"        Increased neutrophils",
"       </td>",
"       <td>",
"        Bleomycin, busulfan",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Eosinophilic alveolitis",
"       </td>",
"       <td>",
"        Allergic \"eosinophilic\" reactions",
"       </td>",
"       <td>",
"        Nitrofurantoin, penicillin, sulfasalazine, ampicillin, tetracycline, bleomycin, clotrimazole",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lipoid pneumonia",
"       </td>",
"       <td>",
"        Alveolar macrophages contain large&nbsp;empty vacuoles stained by&nbsp;Oil-Red-O",
"       </td>",
"       <td>",
"        Mineral oil nose-drops or laxatives",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        Thesaurismosis",
"       </td>",
"       <td rowspan=\"2\">",
"        Accumulation of numerous large lamellar inclusions mainly in alveolar macrophages",
"       </td>",
"       <td>",
"        Amiodarone",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Potentially all amphiphilic drugs",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     BAL: bronchoalveolar lavage.",
"    </div>",
"    <div class=\"reference\">",
"     Data from King TE, Jr. Interstitial Lung Disease. In: Feinsilver SH, Fein AM. (Eds), Textbook of Bronchoscopy, Williams &amp; Wilkins, Baltimore, 1995, p. 185-220.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
