" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Treatment of anemia due to iron deficiency",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?24/62/25578/contributors\">",
