" <div id=\"topicContent\">",
"  <div id=\"drugTitle\">",
"   Aspirin: Drug information",
"  </div>",
"  <div id=\"lexiTitleImg\">",
"   <img height=\"17\" src=\"./../images/lexiComp/Lexicomp_2012_71x17.myextg\" width=\"71\"/>",
"  </div>",
