var title_f24_9_24732="PAINAD";
var content_f24_9_24732=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=SURG%2F59509&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=SURG%2F59509&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    PAINAD - Pain Assessment in Advanced Dementia scale",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Items*",
"       </td>",
"       <td class=\"subtitle1\">",
"        0",
"       </td>",
"       <td class=\"subtitle1\">",
"        1",
"       </td>",
"       <td class=\"subtitle1\">",
"        2",
"       </td>",
"       <td class=\"subtitle1\">",
"        Score",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Breathing independent of vocalization",
"       </td>",
"       <td>",
"        Normal.",
"       </td>",
"       <td>",
"        Occasional labored breathing. Short period of hyperventilation.",
"       </td>",
"       <td>",
"        Noisy labored breathing. Long period of hyperventilation. Cheyne-Stokes respirations.",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Negative vocalization",
"       </td>",
"       <td>",
"        None.",
"       </td>",
"       <td>",
"        Occasional moan or groan. Low-level speech with a negative or disapproving quality.",
"       </td>",
"       <td>",
"        Repeated troubled calling out. Loud moaning or groaning. Crying.",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Facial expression",
"       </td>",
"       <td>",
"        Smiling or inexpressive.",
"       </td>",
"       <td>",
"        Sad. Frightened. Frown.",
"       </td>",
"       <td>",
"        Facial grimacing.",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Body language",
"       </td>",
"       <td>",
"        Relaxed.",
"       </td>",
"       <td>",
"        Tense. Distressed pacing. Fidgeting.",
"       </td>",
"       <td>",
"        Rigid. Fists clenched. Knees pulled up. Pulling or pushing away. Striking out.",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Consolability",
"       </td>",
"       <td>",
"        No need to console.",
"       </td>",
"       <td>",
"        Distracted or reassured by voice or touch.",
"       </td>",
"       <td>",
"        Unable to console, distract or reassure.",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td colspan=\"4\" style=\"text-align: right;\">",
"        <strong>",
"         Total:",
"        </strong>",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    This pain assessment score can be used to assess pain in demented patients. Patients should be observed for five minutes prior to performing the assessment. Total scores range from 0 to 10, with 10 representing severe pain.",
"    <div class=\"footnotes\">",
"     * Five-item observational tool.",
"     <br>",
"      <span class=\"bullet\">",
"       &bull;",
"      </span>",
"      Total scores range from 0 to 10 (based on a scale of 0 to 2 for five items), with a higher score indicating more severe pain (0 = \"no pain\" to 10 = \"severe pain\").",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Original figure modified for this publication. Warden V, Hurley AC, Volicer L. Development and psychometric evaluation of the pain assessment in advanced dementia (PAINAD) scale. J Am Med Dir Assoc 2003; 4:9. Illustration used with the permission of Elsevier Inc. All rights reserved.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
