var title_f25_10_25769="Histologic images of healed plaque rupture";
var content_f25_10_25769=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F63329&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F63329&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\" style=\"width: 580px\">",
"   <div class=\"ttl\">",
"    Histologic images of healed plaque rupture",
"   </div>",
"   <div class=\"cntnt\" style=\"width: 560px; height: 425px; background-image: url(data:image/jpeg;base64,);\">",
"   </div>",
"   <div class=\"lgnd\">",
"    (A) Cross-sectional histologic image of healed plaque rupture. (Movat, magnification x40). Necrotic core (NC) is covered by thick fibrous cap.",
"    <br>",
"     (B) Magnified image (x200) of the inset in panel A. Type III collagen is overlying the rupture site (yellow arrow).",
"     <br>",
"      (C) Sirius red staining corresponding to panel B under the polarized light, the NC is identified as a black area contained within the type III collagen layer (green under polarized light) of the fibrous cap.",
"      <br>",
"       (D) Healed plaque rupture with severe luminal narrowing (Movat, magnification x40).",
"       <br>",
"        (E, F) Sirius red stained sections corresponding to panel D under the normal (E) and polarized light (F), respectively.",
"        <br>",
"         (G, H, I) Magnified image (x200) of the insets in panel D. There are multiple layered NCs, which ruptured at various times prior to the eventual multi-layered appearance, which indicate that repeated episodes of plaque rupture had occurred.",
"         <div class=\"footnotes\">",
"          NC: necrotic core.",
"         </div>",
"         <div class=\"reference\">",
"          Courtesy of Drs. Renu Virmani, Frank Kolodgie, and Fumiyuki Otsuka.",
"         </div>",
"        </br>",
"       </br>",
"      </br>",
"     </br>",
"    </br>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
