var title_f25_14_25835="Maternal blood lead follow-up testing during lactation";
var content_f25_14_25835=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F60025&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F60025&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Frequency of maternal blood lead follow-up testing during lactation to assess risk for infant lead exposure from maternal breast milk*",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Initial venous blood lead level",
"        <span class=\"small\">",
"         &bull;",
"        </span>",
"        (BLL; &micro;g/dL)",
"       </td>",
"       <td class=\"subtitle1\">",
"        Perform follow-up blood lead test(s)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        5-19",
"       </td>",
"       <td>",
"        Every 3 months, unless infant blood lead levels are rising or fail to decline.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        20-39",
"       </td>",
"       <td>",
"        2 weeks postpartum and then at 1- to 3-month intervals depending on direction/magnitude of trend in infant BLLs.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &ge;40",
"       </td>",
"       <td>",
"        Within 24 hours postpartum and then at frequent intervals depending on clinical interventions and trend in BLLs. Consultation with a clinician experienced in the management of lead poisoning is advised.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * If a woman becomes pregnant while lactating, she should be followed according to the schedule for pregnancy.",
"     <br>",
"      <span class=\"bullet\">",
"       &bull;",
"      </span>",
"      Last blood lead level measured in pregnancy or at delivery (maternal or cord BLL).",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced from: Ettinger AS, Wengrovitz AG. Guidelines for the Identification and Management of Lead Exposure in Pregnant and Lactating Women. Centers for Disease Control and Prevention. November 2010.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
