var title_f25_16_25868="Drugs for acute vertigo";
var content_f25_16_25868=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F68052&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F68052&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Drugs for acute vertigo",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Drug",
"      </td>",
"      <td class=\"subtitle1\">",
"       Dose",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\">",
"       Antihistamines",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Dimenhydrinate",
"      </td>",
"      <td>",
"       50 mg every four to six hours",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Diphenhydramine",
"      </td>",
"      <td>",
"       25 to 50 mg every four to six hours",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Meclizine",
"      </td>",
"      <td>",
"       25 to 50 mg every six hours",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\">",
"       Benzodiazepines",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Alprazolam",
"      </td>",
"      <td>",
"       0.5 mg immediate release every eight hours",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Clonazepam",
"      </td>",
"      <td>",
"       0.25 to 0.5 mg every eight hours",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Diazepam",
"      </td>",
"      <td>",
"       5 to 10 mg every twelve hours",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Lorazepam",
"      </td>",
"      <td>",
"       1 to 2 mg every eight hours",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\">",
"       Antiemetics",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Domperidone",
"      </td>",
"      <td>",
"       10 to 20 mg every six to eight hours",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Metoclopramide",
"      </td>",
"      <td>",
"       5 to 10 mg every six hours",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Ondansetron",
"      </td>",
"      <td>",
"       8 mg every twelve hours",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Prochlorperazine",
"      </td>",
"      <td>",
"       5 to 10 mg every six hours",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\">",
"       For acute emergency ward use:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle3_left\" colspan=\"2\">",
"       Antihistamines",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Diphenhydramine",
"      </td>",
"      <td>",
"       10 to 50 mg IM or IV",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle3_left\" colspan=\"2\">",
"       Antiemetics",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Metoclopramide",
"      </td>",
"      <td>",
"       10 to 20 mg IM",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Ondansetron",
"      </td>",
"      <td>",
"       4 mg IM or IV",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Prochlorperazine",
"      </td>",
"      <td>",
"       5 to 10 mg IM or IV",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Promethazine",
"      </td>",
"      <td>",
"       10 to 50 mg IM or IV",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     IM: intramuscular; IV: intravenous.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
