" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Flow-volume loops",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?25/17/25878/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?25/17/25878/contributors\">",
"     Loutfi Sami Aboussouan, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?25/17/25878/contributors\">",
"     James K Stoller, MS, MD",
"    </a>",
"    <br/>",
"   </div>",
