" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Evaluation of the patient with hand pain",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?25/17/25879/contributors\">",
