var title_f25_2_25643="Criteria obsessive personality";
var content_f25_2_25643=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F72308&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F72308&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Criteria for obsessive-compulsive personality disorder",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Four or more of the following plus general criteria for personality disorder:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       1. Is preoccupied with details, rules, lists, order, organization, or schedules to the extent that the major point of the activity is lost",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       2. Shows perfectionism that interferes with task completion",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       3. Is excessively devoted to work and productivity to the exclusion of leisure activities and friendships",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       4. Is overconscientious, scrupulous, and inflexible about matters of morality, ethics, or values (not accounted for by cultural or religious identification)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       5. Is unable to discard worn-out or worthless objects even when they have no sentimental value",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       6. Is reluctant to delegate tasks or to work with others unless they submit to exactly his or her way of doing things",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       7. Adopts a miserly spending style toward both self and others; money is viewed as something to be hoarded for future catastrophes",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       8. Shows rigidity and stubbornness",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from American Psychiatric Association. Diagnostic and Statistical Manual of Mental Disorders, 4th ed, Text revision (DSM-IV-TR). American Psychiatric Association, Washington, DC, 2000.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
