"      Furadantina (AR, MX);",
"     </li>",
"     <li>",
"      Furadantine (FR, LU);",
"     </li>",
"     <li>",
"      Furadantine MC (BE);",
"     </li>",
"     <li>",
"      Furadantine-MC (NL, PT);",
"     </li>",
"     <li>",
"      Furadin (PK);",
"     </li>",
"     <li>",
"      Furadina (VE);",
"     </li>",
"     <li>",
"      Furanpur (UY);",
"     </li>",
"     <li>",
"      Furantoina (ES);",
"     </li>",
"     <li>",
"      Furobactina (ES);",
"     </li>",
"     <li>",
"      Furolin (GR);",
"     </li>",
"     <li>",
"      Macrobid (GB, IE);",
"     </li>",
"     <li>",
"      Macrodantin (AU, GB, IL, IT, PH, ZA);",
"     </li>",
