" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Assessment and management of the acutely agitated or violent adult",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?25/22/25962/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?25/22/25962/contributors\">",
"     Gregory Moore, MD, JD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?25/22/25962/contributors\">",
"     James A Pfaff, MD, FACEP, FAAEM",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?25/22/25962/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?25/22/25962/contributors\">",
"     Robert S Hockberger, MD, FACEP",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?25/22/25962/contributors\">",
"     Deputy Editor",
