var title_f25_22_25964="Spectrum of immune compromise";
var content_f25_22_25964=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F56079&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F56079&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Spectrum of immune compromise",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Significantly immunocompromising conditions include:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hematopoietic stem cell transplant (HCT) within the past two years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Solid organ transplant (SOT) within the past year",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Treatment for rejection after SOT",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Active leukemia or lymphoma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Generalized malignancy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Aplastic anemia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Graft versus host disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Congenital immunodeficiency",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Recent radiation therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Significantly immunosuppressive medications",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        AIDS with low CD4 count",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Moderately immunocompromising conditions include:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Chronic hepatic disease (cirrhosis and alcoholism)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Chronic renal disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Diabetes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Asplenia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Nutritional deficiencies (depending on the nature of the deficiency)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Minimally immunocompromising conditions include:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Chemotherapy for leukemia/lymphoma or cancer more than&nbsp;three months earlier",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Malignancy in remission",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        High dose steroid use more than a month earlier",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mild steroid use including inhaled, topical, intra-articular, bursal, or tendon injection",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        HIV with &gt;500 CD4 lymphocytes/mm3",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        HCT recipients more than&nbsp;two years post-transplant who are not on immunosuppressive drugs and do not have graft versus host disease",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     References:",
"     <ol>",
"      <li>",
"       Advising Travelers with Specific Needs: The Immunocompromised Traveler.",
"       <a href=\"file://wwwnc.cdc.gov/travel/yellowbook/2012/chapter-8-advising-travelers-with-specific-needs/immunocompromised-travelers.htm\">",
"        file://wwwnc.cdc.gov/travel/yellowbook/2012/chapter-8-advising-travelers-with-specific-needs/immunocompromised-travelers.htm",
"       </a>",
"       (Accessed on September 17, 2008).",
"      </li>",
"     </ol>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
