" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Diagnostic challenge and desensitization protocols for NSAID reactions",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?25/25/26006/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?25/25/26006/contributors\">",
"     Ronald A Simon, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?25/25/26006/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?25/25/26006/contributors\">",
"     N Franklin Adkinson, Jr, MD",
"    </a>",
"    <br/>",
"   </div>",
