var title_f25_25_26011="Criteria for pulmonary metastatic disease in Ewings sarcoma";
var content_f25_25_26011=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F78727&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F78727&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Criteria for pulmonary/pleural metastatic disease as used in the EURO-E.W.I.N.G.99 Ewing sarcoma trial",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        CT chest finding",
"       </td>",
"       <td class=\"subtitle1\">",
"        Interpretation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"centered\">",
"        <p>",
"         One or more pulmonary/pleural nodule(s) &gt;1 cm",
"        </p>",
"        <p>",
"         <strong>",
"          OR",
"         </strong>",
"        </p>",
"        <p>",
"         More than one nodule &gt;0.5 cm",
"        </p>",
"       </td>",
"       <td class=\"centered\">",
"        Clear evidence of metastatic disease (in the absence of other clear medical explanation for these lesions).",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"centered\">",
"        <p>",
"         Solitary nodule of 0.5-1 cm",
"        </p>",
"        <p>",
"         <strong>",
"          OR",
"         </strong>",
"        </p>",
"        <p>",
"         Multiple nodules 0.3-0.5 cm",
"        </p>",
"       </td>",
"       <td class=\"centered\">",
"        Questionable evidence of metastatic disease; biopsy proof recommended.",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"centered\">",
"        <p>",
"         Solitary nodule &lt;0.5 cm",
"        </p>",
"        <p>",
"         <strong>",
"          OR",
"         </strong>",
"        </p>",
"        <p>",
"         Several nodules &lt;0.3 cm",
"        </p>",
"       </td>",
"       <td class=\"centered\">",
"        Not clear evidence of lung disease. Individual decision-making regarding biopsy.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
