" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Unipolar minor depression in adults: Epidemiology, clinical presentation, and diagnosis",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?25/26/26025/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?25/26/26025/contributors\">",
"     Wayne Katon, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?25/26/26025/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?25/26/26025/contributors\">",
"     Peter P Roy-Byrne, MD",
"    </a>",
"    <br/>",
"   </div>",
