" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Diagnostic approach to community-acquired pneumonia in adults",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?25/26/26026/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?25/26/26026/contributors\">",
"     John G Bartlett, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?25/26/26026/contributors\">",
"     Section Editor",
"    </a>",
