var title_f25_31_26108="Clin pulm infect score";
var content_f25_31_26108=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F77054&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F77054&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Clinical Pulmonary Infection Score (CPIS)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Temperature",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       &ge;36.5 or &le;38.4 = 0 point",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       &ge;38.5 or &le;38.9 = 1 point",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       &ge;39 or &lt;36.5 = 2 points",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Blood leukocytes, microL",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       &ge;4000 or &le;11,000 = 0 points",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       &lt;4000 or &gt;11,000 = 1 point",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Band forms &ge;50 percent = add 1 point",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Tracheal secretions",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Absence of tracheal secretions = 0 point",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Presence of non-purulent tracheal secretions = 1 point",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Presence of purulent tracheal secretions = 2 points",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Oxygenation",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       PaO2/FIO2, mmHg &gt;240 or ARDS (defined as PaO2/FIO2 &le;200, PAWP &le;18 mmHg and acute bilateral infiltrates) = 0 points",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       PaO2/FIO2 &le;240 and no ARDS = 2 points",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Pulmonary radiography",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       No infiltrate = 0 point",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Diffuse (patchy) infiltrate = 1 point",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Localized infiltrate = 2 points",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Progression of pulmonary infiltrate",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       No radiographic progression = 0 point",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Radiographic progression (after HF and ARDS excluded) = 2 points",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Culture of tracheal aspirate",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Pathogenic bacteria cultured in rare or few quantities or no growth = 0 point",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Pathogenic bacteria cultured in moderate or heavy quantity = 1 point",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Same pathogenic bacteria seen on Gram's stain, add 1 point",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Total (a score of &gt;6 was considered suggestive of pneumonia)",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     ARDS: acute respiratory distress syndrome; HF: heart failure; PAWP: pulmonary arterial wedge pressure.",
"     <br>",
"      An initial score is based upon the first five variables. The last two variables are assessed on day 2 or 3.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Adapted with permission from: Singh, N, Rogers, P, Atwood, CW, et al. Short-course empiric antibiotic therapy for patients with pulmonary infiltrates in the intensive care unit: a proposed solution for indiscriminate antibiotic prescription. Am J Respir Crit Care Med 2000; 162:505. Copyright &copy; 2002 American Thoracic Society.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
