" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Auscultation of cardiac murmurs",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?25/37/26202/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?25/37/26202/contributors\">",
"     Kanu Chatterjee, MB, FRCP, FCCP, FACC, MACP",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?25/37/26202/contributors\">",
"     Section Editor",
