" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Adjuvant therapy of early stage (stage I and II) epithelial ovarian, fallopian tubal, or peritoneal cancer",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?25/39/26231/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?25/39/26231/contributors\">",
"     Thomas J Herzog, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?25/39/26231/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?25/39/26231/contributors\">",
"     Barbara Goff, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?25/39/26231/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?25/39/26231/contributors\">",
"     Don S Dizon, MD, FACP",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
