var title_f25_43_26300="Atypical antidepressants - Doses for treating depression";
var content_f25_43_26300=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PSYCH%2F88065&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PSYCH%2F88065&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Atypical antidepressants: Doses for treating unipolar major depression",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"4\" width=\"25%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Drug",
"       </td>",
"       <td class=\"subtitle1\">",
"        Usual starting dose",
"        <br/>",
"        (mg per day)",
"       </td>",
"       <td class=\"subtitle1\">",
"        Usual daily dose",
"        <br/>",
"        (mg)",
"       </td>",
"       <td class=\"subtitle1\">",
"        Extreme daily dose",
"        <br/>",
"        (mg)*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Agomelatine",
"       </td>",
"       <td class=\"centered\">",
"        25",
"       </td>",
"       <td class=\"centered\">",
"        25 to 50",
"       </td>",
"       <td class=\"centered\">",
"        25 to 50",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\" colspan=\"4\">",
"        Bupropion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Immediate release",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"       <td class=\"centered\">",
"        200",
"       </td>",
"       <td class=\"centered\">",
"        300",
"       </td>",
"       <td class=\"centered\">",
"        100 to 450",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Sustained release",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td class=\"centered\">",
"        150",
"       </td>",
"       <td class=\"centered\">",
"        300",
"       </td>",
"       <td class=\"centered\">",
"        150 to 400",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Extended release",
"        <sup>",
"         &loz;",
"        </sup>",
"       </td>",
"       <td class=\"centered\">",
"        150",
"       </td>",
"       <td class=\"centered\">",
"        300",
"       </td>",
"       <td class=\"centered\">",
"        150 to 450",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mirtazapine",
"       </td>",
"       <td class=\"centered\">",
"        15",
"       </td>",
"       <td class=\"centered\">",
"        15 to 45",
"       </td>",
"       <td class=\"centered\">",
"        7.5 to 60",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Lower doses are often used for elderly, medically compromised (eg, renal or liver disease), or drug sensitive patients, as well as patients with a lower body mass index. Higher doses may be used for medications that are well tolerated but ineffective at lower doses.",
"     <br/>",
"     <span class=\"bullet\">",
"      &bull;",
"     </span>",
"     Maximum single dose: 150 mg per day.",
"     <br/>",
"     &Delta; Maximum single dose: 200 mg per day.",
"     <br/>",
"     <span class=\"lozenge\">",
"      &loz;",
"     </span>",
"     Maximum single dose: 300 mg per day in Europe; 450 mg per day in the United States.",
"    </div>",
"    <div class=\"reference\">",
"     Data from:",
"     <br/>",
"     <ol>",
"      <li>",
"       Gartlehner G, Thaler K, Hill S, Hansen RA. How should primary care doctors select which antidepressants to administer? Curr Psychiatry Rep 2012; 14:360.",
"      </li>",
"      <li>",
"       Labbate LA, Fava M, Rosenbaum JF, Arana GW. Drugs for the treatment of depression. In: Handbook of Psychiatric Drug Therapy, 6th Ed, Lippincott Williams and Wilkins, Philadelphia 2010, p.54.",
"      </li>",
"     </ol>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
var script_f25_43_26300=[""].join("\n");
