var title_f25_43_26301="TNM staging bone tumors";
var content_f25_43_26301=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F62124&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F62124&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Definition of TNM for bone tumors other than lymphoma and myeloma",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"5\">",
"        Primary tumor (T)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        TX",
"       </td>",
"       <td colspan=\"4\">",
"        Primary tumor cannot be assessed",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        T0",
"       </td>",
"       <td colspan=\"4\">",
"        No evidence of primary tumor",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        T1",
"       </td>",
"       <td colspan=\"4\">",
"        Tumor 8 cm or less in greatest dimension",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        T2",
"       </td>",
"       <td colspan=\"4\">",
"        Tumor more than 8 cm in greatest dimension",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        T3",
"       </td>",
"       <td colspan=\"4\">",
"        Discontinuous tumors in the primary bone site",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"5\">",
"        Regional lymph nodes (N)*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        NX",
"       </td>",
"       <td colspan=\"4\">",
"        Regional lymph nodes cannot be assessed",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        N0",
"       </td>",
"       <td colspan=\"4\">",
"        No regional lymph node metastasis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        N1",
"       </td>",
"       <td colspan=\"4\">",
"        Regional lymph node metastasis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"5\">",
"        Distant metastasis (M)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        M0",
"       </td>",
"       <td colspan=\"4\">",
"        No distant metastasis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        M1",
"       </td>",
"       <td class=\"sublist_other_start\" colspan=\"4\">",
"        Distant metastasis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        M1a",
"       </td>",
"       <td class=\"sublist_other\" colspan=\"4\">",
"        Lung",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        M1b",
"       </td>",
"       <td class=\"sublist_other\" colspan=\"4\">",
"        Other distant sites",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"5\">",
"        Histologic grade (G)",
"        <span class=\"small\">",
"         &bull;",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"5\">",
"        Grade is reported in registry systems by the grade value. A two-grade, three-grade, or four-grade system may be used. If a grading system is not specified, generally the following system is used:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        GX",
"       </td>",
"       <td colspan=\"4\">",
"        Grade cannot be assessed",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        G1",
"       </td>",
"       <td colspan=\"4\">",
"        Well differentiated - low grade",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        G2",
"       </td>",
"       <td colspan=\"4\">",
"        Moderately differentiated - low grade",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        G3",
"       </td>",
"       <td colspan=\"4\">",
"        Poorly differentiated - high grade",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        G4",
"       </td>",
"       <td colspan=\"4\">",
"        Undifferentiated - high grade",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"5\">",
"        Anatomic stage/prognostic groups",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Stage IA",
"       </td>",
"       <td>",
"        T1",
"       </td>",
"       <td>",
"        N0",
"       </td>",
"       <td>",
"        M0",
"       </td>",
"       <td>",
"        G1, 2 Low grade, GX",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        Stage IB",
"       </td>",
"       <td>",
"        T2",
"       </td>",
"       <td>",
"        N0",
"       </td>",
"       <td>",
"        M0",
"       </td>",
"       <td>",
"        G1, 2 Low grade, GX",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        T3",
"       </td>",
"       <td>",
"        N0",
"       </td>",
"       <td>",
"        M0",
"       </td>",
"       <td>",
"        G1, 2 Low grade, GX",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Stage IIA",
"       </td>",
"       <td>",
"        T1",
"       </td>",
"       <td>",
"        N0",
"       </td>",
"       <td>",
"        M0",
"       </td>",
"       <td>",
"        G3, 4 High grade",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Stage IIB",
"       </td>",
"       <td>",
"        T2",
"       </td>",
"       <td>",
"        N0",
"       </td>",
"       <td>",
"        M0",
"       </td>",
"       <td>",
"        G3, 4 High grade",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Stage III",
"       </td>",
"       <td>",
"        T3",
"       </td>",
"       <td>",
"        N0",
"       </td>",
"       <td>",
"        M0",
"       </td>",
"       <td>",
"        G3, 4 High grade",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Stage IVA",
"       </td>",
"       <td>",
"        Any T",
"       </td>",
"       <td>",
"        N0",
"       </td>",
"       <td>",
"        M1a",
"       </td>",
"       <td>",
"        Any G",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        Stage IVB",
"       </td>",
"       <td>",
"        Any T",
"       </td>",
"       <td>",
"        N1",
"       </td>",
"       <td>",
"        Any M",
"       </td>",
"       <td>",
"        Any G",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Any T",
"       </td>",
"       <td>",
"        Any N",
"       </td>",
"       <td>",
"        M1b",
"       </td>",
"       <td>",
"        Any G",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    Note: cTNM is the clinical classification, pTNM is the pathologic classification.",
"    <div class=\"footnotes\">",
"     * Because of the rarity of lymph node involvement in bone sarcomas, the designation NX may not be appropriate and cases should be considered N0 unless clinical node involvement is clearly evident.",
"     <br>",
"      <span class=\"bullet\">",
"       &bull;",
"      </span>",
"      Ewing's sarcoma is classified as G4.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Used with the permission of the American Joint Committee on Cancer (AJCC), Chicago, Illinois. The original source for this material is the AJCC Cancer Staging Manual, Seventh Edition (2010) published by Springer New York, Inc.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
