" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Medical management of patients after bariatric surgery",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?25/47/26362/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
