var title_f25_50_26412="Vitamin intake in CKD";
var content_f25_50_26412=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEPH%2F73623&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEPH%2F73623&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Recommended vitamin intake for patients with chronic kidney and end-stage renal disease",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\" rowspan=\"2\">",
"        Vitamins",
"       </td>",
"       <td class=\"subtitle1\">",
"        Chronic kidney disease*",
"       </td>",
"       <td class=\"subtitle1\">",
"        Maintenance hemodialysis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle3\" colspan=\"2\">",
"        Diets to be supplemented with the following quantities",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Thiamin, mg/day",
"       </td>",
"       <td>",
"        1.5",
"       </td>",
"       <td>",
"        1.1-1.2",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Riboflavin, mg/day",
"       </td>",
"       <td>",
"        1.8",
"       </td>",
"       <td>",
"        1.1-1.3",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pantothenic acid, mg/day",
"       </td>",
"       <td>",
"        5",
"       </td>",
"       <td>",
"        5",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Niacin, mg/day",
"       </td>",
"       <td>",
"        20",
"       </td>",
"       <td>",
"        14-16",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pyridoxine, mg/day",
"       </td>",
"       <td>",
"        5",
"       </td>",
"       <td>",
"        10",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vitamin B12, &micro;g/day",
"       </td>",
"       <td>",
"        3",
"       </td>",
"       <td>",
"        2.4",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vitamin C, mg/day",
"       </td>",
"       <td>",
"        60",
"       </td>",
"       <td>",
"        75-90",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Folic acid mg, day",
"       </td>",
"       <td>",
"        1",
"       </td>",
"       <td>",
"        1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vitamin A",
"       </td>",
"       <td>",
"        No addition",
"       </td>",
"       <td>",
"        No addition",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vitamin D",
"       </td>",
"       <td>",
"        &bull;",
"       </td>",
"       <td>",
"        &bull;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vitamin E, IU/day",
"       </td>",
"       <td>",
"        15",
"       </td>",
"       <td>",
"        400-800",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vitamin K",
"       </td>",
"       <td>",
"        None",
"       </td>",
"       <td>",
"        None",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    The nutritional intake is adjusted based upon individual patient needs.",
"    <div class=\"footnotes\">",
"     * GFR &lt;70 mL/min/1.73m2 with evidence for progression.",
"     <br>",
"      <span class=\"bullet\">",
"       &bull;",
"      </span>",
"      Vitamin D, given as calcitriol, is adjusted according to phosphate and calcium balance.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Data from:",
"     <br>",
"      <ol>",
"       <li>",
"        Ahmed, K, Kopple, J. Nutritional management of renal disease. In: Primer on Kidney Diseases, Greenberg, A (Ed). Academic Press, San Diego, CA, 1994, p. 289.",
"       </li>",
"       <li>",
"        Tattersall, J, Martin-Malo, A, Pedrini, L, et al. European Best Practice Guidelines on Haenodialysis. Nephrol Dial Transplant 2007; 22(Suppl 2):62.",
"       </li>",
"      </ol>",
"     </br>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
