" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Treatment of cutaneous leishmaniasis",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"./treatment-of-cutaneous-leishmaniasis/contributors\">",
