var title_f25_53_26459="Signs symptoms breast CA recur";
var content_f25_53_26459=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F80826&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F80826&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Signs and symptoms consistent with breast cancer relapse",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Locoregional recurrence",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mass in the ipsilateral breast following breast conserving therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mass in the chest wall after mastectomy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Nipple discharge in the treated breast following breast conserving therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Rash localized to the treated breast or chest wall",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Axillary, supraclavicular, infraclavicular, or cervical lymph node enlargement",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Systemic recurrence",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Skeletal relapse: localized, progressive bone pain or tenderness",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pulmonary metastasis: pleuritic chest pain, cough, dyspnea",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Liver relapse: right upper quadrant discomfort, fullness, or pain; weight loss; anorexia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        CNS metastasis: persistent headache, mental status changes, new onset seizure, focal motor or sensory loss, bladder or bowel dysfunction",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     CNS: central nervous system.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Emens LA, Davidson NE. The follow-up of breast cancer. Semin Oncol 2003; 30:338. Copyright &copy; 2003 Elsevier.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
