var title_f25_54_26475="Red flag symptoms of low back pain";
var content_f25_54_26475=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F68550&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F68550&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    \"Red flags\" for a potentially serious underlying cause for low back pain",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Trauma, cumulative trauma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Unexplained weight loss",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Age &gt;50 years, especially women, and males with osteoporosis or compression fracture",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Unexplained fever, history of urinary or other infections",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Immunosuppression, or diabetes mellitus",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        History of cancer",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Intravenous (IV) drug use",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Prolonged use of corticosteroids, osteoporosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Age &gt;70",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Focal neurologic deficit(s) with progressive or disabling symptoms, cauda equina syndrome",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Duration longer than six weeks",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Prior surgery",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Low back pain. American College of Radiology. ACR Appropriateness Criteria. Copyright &copy; 2011 American College of Radiology. The complete version of the ACR Appropriateness Criteria&reg; Low Back Pain can be accessed on the ACR website at",
"     <a href=\"file://www.acr.org\" target=\"_blank\">",
"      www.acr.org",
"     </a>",
"     .",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
