var title_f25_55_26491="Prev HD cath infection";
var content_f25_55_26491=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F51737&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F51737&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Prevention of infection with central hemodialysis catheters",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Replacement and relocation of catheter",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Do not routinely replace hemodialysis catheters.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Replacement of catheter-site dressing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Replace dressing when the catheter is removed or replaced, or when the dressing becomes damp, loosened, or soiled. Replace dressings more frequently in diaphoretic patients.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        There is no need to remove a gauze or opaque dressing if the patient has no clinical signs of infection.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Replacement of administration sets",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Use hemodialysis catheters solely for hemodialysis. Use of hemodialysis catheters for other purposes (eg, administration of fluids, blood/blood products, or parenteral nutrition) should be restricted to circumstances where no alternative vascular access is feasible.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Hang time for parenteral fluids",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Use hemodialysis catheters solely for hemodialysis. Use of hemodialysis catheters for other purposes (eg, administration of fluids, blood/blood products, or parenteral nutrition) should be restricted to circumstances where no alternative vascular access is feasible.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
