var title_f25_6_25708="Common pain behav elderly";
var content_f25_6_25708=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F58023&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F58023&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Common pain behaviors in cognitively impaired elderly persons",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Facial expressions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Slight frown; sad, frightened face",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Grimacing, wrinkled forehead, closed or tightened eyes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Any distorted expression",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Rapid blinking",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Verbalizations, vocalizations",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sighing, moaning, groaning",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Grunting, chanting, calling out",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Noisy breathing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Asking for help",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Verbally abusive",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Body movements",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Rigid, tense body posture, guarding",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Fidgeting",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Increased pacing, rocking",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Restricted movement",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Gait or mobility changes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Changes in interpersonal interactions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Aggressive, combative, resisting care",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Decreased social interactions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Socially inappropriate, disruptive",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Withdrawn",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Changes in activity patterns or routines",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Refusing food, appetite change",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Increase in rest periods",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sleep, rest pattern changes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sudden cessation of common routines",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Increased wandering",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Mental status changes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Crying or tears",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Increased confusion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Irritability or distress",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    Some patients demonstrate little or no specific behavior associated with severe pain.",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     AGS Panel on Persistent Pain in Older Persons",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
var script_f25_6_25708=[""].join("\n");
var outline_f25_6_25708=null;
var title_f25_6_25709="Risk factors breast ca 40 to 49";
var content_f25_6_25709=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F83694&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F83694&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Factors significantly associated with increased breast cancer risk for women aged 40 to 49 years",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Risk factor",
"       </td>",
"       <td class=\"subtitle1\">",
"        Breast cancer risk ratio",
"        <br/>",
"        (95 percent CI)",
"       </td>",
"       <td class=\"subtitle1\">",
"        Source",
"        <br/>",
"        (reference)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        &ge;2-fold increased risk",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\" colspan=\"3\">",
"        First-degree relatives with breast cancer",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        1",
"       </td>",
"       <td class=\"sublist_other\">",
"        2.14 (1.92-2.38)",
"       </td>",
"       <td class=\"sublist_other\">",
"        1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        2",
"       </td>",
"       <td class=\"sublist_other\">",
"        3.84 (2.37-6.22)",
"       </td>",
"       <td class=\"sublist_other\">",
"        1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\" colspan=\"3\">",
"        Age of first-degree relative with breast cancer",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        &lt;40 years",
"       </td>",
"       <td class=\"sublist_other\">",
"        3.0 (1.8-4.9)",
"       </td>",
"       <td class=\"sublist_other\">",
"        1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        &lt;50 years",
"       </td>",
"       <td class=\"sublist_other\">",
"        2.17 (1.86-2.53)",
"       </td>",
"       <td class=\"sublist_other\">",
"        BCSC",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Breast density BI-RADS category 4",
"       </td>",
"       <td>",
"        2.04 (1.84-2.26)",
"       </td>",
"       <td>",
"        2*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        1.5- to 2.0-fold increased risk",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Prior benign breast biopsy result",
"       </td>",
"       <td>",
"        1.87 (1.64-2.13)",
"       </td>",
"       <td>",
"        3*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Second-degree relative with breast cancer",
"       </td>",
"       <td>",
"        1.7 (1.4-2.0)",
"       </td>",
"       <td>",
"        4",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Breast density BI-RADS category 3",
"       </td>",
"       <td>",
"        1.62 (1.51-1.75)",
"       </td>",
"       <td>",
"        2*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        1.0- to 1.5-fold increased risk",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Current oral contraceptive use",
"       </td>",
"       <td>",
"        1.30 (1.13-1.49)",
"       </td>",
"       <td>",
"        BCSC",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Nulliparity",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"       <td>",
"        1.25 (1.08-1.46)",
"       </td>",
"       <td>",
"        5-9",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Age at first birth &ge;30 years",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td>",
"        1.20 (1.02-1.42)",
"       </td>",
"       <td>",
"        5-9",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <p>",
"     &nbsp;",
"    </p>",
"    <div class=\"footnotes\">",
"     BCSC: Breast Cancer Surveillance Consortium; BI-RADS: Breast Imaging Reporting and Data System.",
"     <br/>",
"     * BCSC estimates from published analyses.",
"     <br/>",
"     <span class=\"bullet\">",
"      &bull;",
"     </span>",
"     Nulliparity was calculated in two ways. Estimates indicating significantly-increased risk for nonparous compared with parous women were similar for the meta-analysis and the BCSC data. Estimates comparing ages at first birth that included nonparous women provided significant results for the meta-analysis only.",
"     <br/>",
"     &Delta; Results were not statistically significant for the BCSC data.",
"    </div>",
"    <div class=\"reference\">",
"     References:",
"     <ol>",
"      <li>",
"       Collaborative Group on Hormonal Factors in Breast Cancer. Familial breast cancer: Collaborative reanalysis of individual data from 52 epidemiological studies including 58,209 women with breast cancer and 101,986 women without the disease. Lancet 2001; 358:1389.",
"      </li>",
"      <li>",
"       Kerlikowske K, Cook AJ, Buist DS, et al. Breast cancer risk by breast density, menopause, and postmenopausal hormone therapy use. J Clin Oncol 2010; 28:3830.",
"      </li>",
"      <li>",
"       Ashbeck EL, Rosenberg RD, Stauber PM, Key CR. Benign breast biopsy diagnosis and subsequent risk of&nbsp;breast cancer. Cancer Epidemiol Biomarkers Prev 2007; 16:467.",
"      </li>",
"      <li>",
"       Pharoah PD, Day NE, Duffy S, et al. Family history and the risk of breast cancer: A systematic review and meta-analysis. Int J Cancer 1997; 71:800.",
"      </li>",
"      <li>",
"       McCredie M, Paul C, Skegg DC, Williams S. Reproductive factors and breast cancer in New Zealand. Int J Cancer 1998; 76:182.",
"      </li>",
"      <li>",
"       Chen CL, White E, Malone KE, Daling JR. Leisure-time physical activity in relation to breast cancer among young women (Washington, United States). Cancer Causes Control 1997; 8:77.",
"      </li>",
"      <li>",
"       Althuis MD, Brogan DD, Coates RJ, et al. Breast cancers among very young premenopausal women (United States). Cancer Causes Control 2003; 14:151.",
"      </li>",
"      <li>",
"       Sweeney C, Baumgartner KB, Byers T, et al. Reproductive history in relation to breast cancer risk among Hispanic and non-Hispanic white women. Cancer Causes Control 2008; 19:391.",
"      </li>",
"      <li>",
"       Reinier KS, Vacek PM, Geller BM. Risk factors for breast carcinoma in situ versus invasive breast cancer in a prospective study of pre- and post-menopausal women. Breast Cancer Res Treat 2007; 103:343.",
"      </li>",
"     </ol>",
"     <p>",
"      Reproduced with permission from: Nelson HD, Zakher B, Cantor A, et al. Risk Factors for Breast Cancer for Women Aged 40 to 49 Years. Ann Intern Med 2012; 156:635. Copyright &copy; 2012 American College of Physicians.",
"     </p>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
var script_f25_6_25709=[""].join("\n");
var outline_f25_6_25709=null;
var title_f25_6_25710="Gem plus doc for advanced sarcoma";
var content_f25_6_25710=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F58713&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F58713&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Gemcitabine plus docetaxel for advanced sarcoma",
"    <sup>",
"     [1]",
"    </sup>",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"4\" width=\"25%\">",
"     </colgroup>",
"     <tbody>",
"      <tr class=\"divider_bottom\">",
"       <td colspan=\"4\">",
"        Cycle length: 21 days.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Drug",
"       </td>",
"       <td class=\"subtitle1\">",
"        Dose and route",
"       </td>",
"       <td class=\"subtitle1\">",
"        Administration",
"       </td>",
"       <td class=\"subtitle1\">",
"        Given on days",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Gemcitabine",
"        </strong>",
"       </td>",
"       <td>",
"        900 mg/m",
"        <sup>",
"         2",
"        </sup>",
"        IV (for soft tissue sarcoma if no prior pelvic radiation therapy [RT]*)",
"       </td>",
"       <td>",
"        Dilute with 100 mL normal saline (NS) (final concentration &lt;40 mg/mL) and administer over 90 minutes (ie, 10 mg/m",
"        <sup>",
"         2",
"        </sup>",
"        per min).",
"       </td>",
"       <td>",
"        Days 1 and 8",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Docetaxel",
"        </strong>",
"       </td>",
"       <td>",
"        75 or 100 mg/m",
"        <sup>",
"         2",
"        </sup>",
"        IV (if no prior pelvic RT)",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td>",
"        Dilute with 250 mL NS (final concentration of 0.3 to 0.74 mg/mL) and administer over 60 minutes.",
"       </td>",
"       <td>",
"        Day 8",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"4\">",
"        Pretreatment considerations:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"4\">",
"        &bull;",
"        <strong>",
"         Emesis risk:",
"        </strong>",
"        MODERATE (30 to 90 percent frequency of emesis). Refer to UpToDate topic on \"Prevention and treatment of chemotherapy-induced nausea and vomiting\".",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"4\">",
"        &bull;",
"        <strong>",
"         Prophylaxis for infusion reactions:",
"        </strong>",
"        Premedicate with dexamethasone prior to docetaxel administration",
"        <sup>",
"         [2]",
"        </sup>",
"        . Refer to UpToDate topic on \"Infusion reactions to systemic chemotherapy\".",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"4\">",
"        &bull;",
"        <strong>",
"         Vesicant/irritant properties:",
"        </strong>",
"        Docetaxel is an irritant but can cause significant tissue damage; avoid extravasation. Refer to UpToDate topic on \"Extravasation injury from chemotherapy and other non-neoplastic vesicants\".",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"4\">",
"        &bull;",
"        <strong>",
"         Infection prophylaxis:",
"        </strong>",
"        This regimen was designed to include primary prophylaxis with granulocyte colony stimulating factors (starting on day 9)",
"        <sup>",
"         [1]",
"        </sup>",
"        . Refer to UpToDate topic on \"Use of granulocyte colony stimulating factors in patients with chemotherapy-induced neutropenia\".",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"4\">",
"        &bull;",
"        <strong>",
"         Dose adjustment for baseline liver or renal dysfunction:",
"        </strong>",
"        Docetaxel should not be administered if serum bilirubin is above the ULN or if the AST and/or ALT are &gt;1.5 times ULN concomitant with alkaline phosphatase &gt;2.5 times ULN",
"        <sup>",
"         [2]",
"        </sup>",
"        . A lower starting dose of gemcitabine may be needed in patients with impaired liver function. Refer to UpToDate topic on \"Chemotherapy hepatotoxicity and dose modification in patients with liver disease\".",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"4\">",
"        Monitoring parameters:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"4\">",
"        &bull; Monitor CBC with differential and platelet count weekly during treatment.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"4\">",
"        &bull; Assess basic metabolic panel (including serum creatinine) and liver function tests prior to each treatment cycle.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"4\">",
"        &bull; Assess for neuropathy, pulmonary toxicity, and fluid retention (weight gain, shortness of breath, peripheral edema, ascites) during treatment.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"4\">",
"        Suggested dose alterations for toxicity:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"4\">",
"        &bull;",
"        <strong>",
"         Myelotoxicity:",
"        </strong>",
"        The original protocol used the following dose adjustment parameters",
"        <sup>",
"         [1]",
"        </sup>",
"        . For febrile neutropenia or a platelet count &lt;25,000/mm",
"        <sup>",
"         3",
"        </sup>",
"        lasting more than five days, reduce dose of both gemcitabine and docetaxel by 25 percent for all subsequent cycles. Delay treatment by one week if the day 1 ANC is &lt;1000 cells/mm",
"        <sup>",
"         3",
"        </sup>",
"        or platelet count &lt;100,000/mm",
"        <sup>",
"         3",
"        </sup>",
"        ; discontinue treatment if not recovered to these levels after two weeks. If day 8 ANC is 500 to 999 cells/mm",
"        <sup>",
"         3",
"        </sup>",
"        or platelet count 50,000 to 99,000/mm",
"        <sup>",
"         3",
"        </sup>",
"        , reduce gemcitabine and docetaxel by 25 percent. If ANC &lt;500 cells/mm",
"        <sup>",
"         3",
"        </sup>",
"        or platelets &lt;50,000/mm",
"        <sup>",
"         3",
"        </sup>",
"        on day 8, delete day 8 doses of both drugs for that cycle.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"4\">",
"        &bull;",
"        <strong>",
"         Neurotoxicity:",
"        </strong>",
"        For grade 3 or 4 neurotoxicity, delay treatment one week; if neurotoxicity resolves to &le;grade 2, resume treatment with docetaxel dose reduced by 25 percent of the previous dose for all subsequent cycles.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"4\">",
"        &bull;",
"        <strong>",
"         Hemolytic-uremic syndrome (HUS):",
"        </strong>",
"        In clinical trials, HUS was reported in 0.25 percent of patients receiving gemcitabine",
"        <sup>",
"         [3]",
"        </sup>",
"        . Consider diagnosis if the patient develops hemolysis and severe thrombocytopenia, with or without renal failure or central nervous system findings. Discontinue gemcitabine immediately and permanently. Refer to UpToDate topic on \"Causes of thrombotic thrombocytopenic purpura-hemolytic uremic syndrome in adults\".",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"4\">",
"        &bull;",
"        <strong>",
"         Pulmonary toxicity:",
"        </strong>",
"        A variety of manifestations of pulmonary toxicity have been reported in patients treated with gemcitabine. Discontinue treatment immediately and permanently. Refer to UpToDate topic on \"Pulmonary toxicity associated with antineoplastic therapy: Cytotoxic agents\".",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"4\">",
"        &bull;",
"        <strong>",
"         Hepatotoxicity:",
"        </strong>",
"        For bilirubin level &gt;1.5 mg/dL, hold docetaxel for that cycle. If bilirubin returns to &lt;1.5 mg/dL, resume docetaxel treatment in subsequent cycles",
"        <sup>",
"         [1]",
"        </sup>",
"        . Gemcitabine is commonly associated with a transient rise in serum transaminases, but these are seldom of clinical significance. There is insufficient information from clinical studies to allow clear dose recommendations in these patients. Refer to UpToDate topic on \"Chemotherapy hepatotoxicity and dose modification in patients with liver disease\".",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"4\">",
"        &bull;",
"        <strong>",
"         Other toxicities:",
"        </strong>",
"        For all other grade 3 or 4 nonhemalogic toxicities (except alopecia), hold treatment for up to two weeks; resume treatment when toxicity has resolved to &lt;grade 2.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"4\">",
"        <strong>",
"         If there is a change in body weight of at least 10 percent, doses should be recalculated.",
"        </strong>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <strong>",
"     This table is provided as an example of how to administer this regimen; there may be other acceptable methods. This regimen must be administered by a clinician trained in the use of chemotherapy. The clinician is expected to use his or her independent medical judgment in the context of individual circumstances to make adjustments, as necessary.",
"    </strong>",
"    <div class=\"footnotes\">",
"     IV: intravenous; ULN: upper limit of normal; AST: aspartate aminotransferase; ALT: alanine transaminase; CBC: complete blood count; ANC: absolute neutrophil count.",
"     <br/>",
"     * In the original protocol, patients with prior pelvic radiation therapy (RT) received 25 percent lower doses of gemcitabine (675 mg/m",
"     <sup>",
"      2",
"     </sup>",
"     on days 1 and 8). This lower gemcitabine dose was also used when this protocol was administered to patients with refractory, advanced osteosarcoma",
"     <sup>",
"      [4]",
"     </sup>",
"     .",
"     <br/>",
"     &Delta; In the original protocol, patients without prior pelvic RT received 100 mg/m",
"     <sup>",
"      2",
"     </sup>",
"     docetaxel, and the dose was reduced to 75 mg/m",
"     <sup>",
"      2",
"     </sup>",
"     for patients with prior pelvic RT. However, given the toxicity seen with this combination in a randomized trial of gemcitabine plus docetaxel",
"     <sup>",
"      [5]",
"     </sup>",
"     , many clinicians consider the 100 mg/m",
"     <sup>",
"      2",
"     </sup>",
"     docetaxel dose to be too high for off-protocol use, even in patients without prior pelvic RT. Off protocol, we give docetaxel 75 mg/m",
"     <sup>",
"      2",
"     </sup>",
"     on day 8 of each 21 day cycle, with a reduction for prior pelvic irradiation",
"     <sup>",
"      [6]",
"     </sup>",
"     . Another alternative is a lower dose weekly docetaxel schedule of 30 mg/m",
"     <sup>",
"      2",
"     </sup>",
"     on days 1, 8, and 15 of each 28 day cycle (with gemcitabine 800 mg/m",
"     <sup>",
"      2",
"     </sup>",
"     on days 1, 8, and 15)",
"     <sup>",
"      [7]",
"     </sup>",
"     . This schedule has not been prospectively examined in sarcomas.",
"    </div>",
"    <div class=\"reference\">",
"     References:",
"     <br/>",
"     <ol>",
"      <li>",
"       Hensley ML, et al. J Clin Oncol 2002; 20:2824.",
"      </li>",
"      <li>",
"       Taxotere&reg; (docetaxel) injection. US FDA-approved package insert. US National Library of Medicine. (Available online at dailymed.nlm.nih.gov, accessed on January 3, 2012).",
"      </li>",
"      <li>",
"       Gemcitabine hydrochloride injection. US FDA-approved package insert. US National Library of Medicine. (Available online at dailymed.nlm.nih.gov, accessed on January 3, 2012).",
"      </li>",
"      <li>",
"       Navid F, et al. Cancer 2008; 113:419.",
"      </li>",
"      <li>",
"       Maki RG, et al. J Clin Oncol 2007; 25:2755.",
"      </li>",
"      <li>",
"       Hensley ML, et al. Gynecol Oncol 2009; 112:563.",
"      </li>",
"      <li>",
"       O'Shaughnessy JA, et al. Clin Breast Cancer 2006; 6:505.",
"      </li>",
"     </ol>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
