var title_f25_9_25755="Clinical features of acute colonic and small bowel ischemia";
var content_f25_9_25755=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F62738&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F62738&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Clinical features of acute colonic and small bowel ischemia",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Acute colonic ischemia",
"       </td>",
"       <td class=\"subtitle1\">",
"        Acute mesenteric ischemia involving small bowel",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        90 percent of patients over age 60 years",
"       </td>",
"       <td>",
"        Age varies with etiology of ischemia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Acute precipitating cause is rare",
"       </td>",
"       <td>",
"        Acute precipitating cause is typical",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Patients do not appear severely ill",
"       </td>",
"       <td>",
"        Patients appear severely ill",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mild abdominal pain, tenderness present",
"       </td>",
"       <td>",
"        Pain is usually severe, tenderness is not prominent early",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Rectal bleeding, bloody diarrhea typical",
"       </td>",
"       <td>",
"        Bleeding uncommon until very late",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Colonoscopy is procedure of choice",
"       </td>",
"       <td>",
"        MRA or MDCT angiography may be considered as the initial diagnostic test, angiography is recommended if there is strong clinical suspicion",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     MDCT: multidetector row computed tomography; MRA: magnetic resonance angiography.",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Reinus JF, Brandt LJ, Boley SJ, Gastroenterol Clin North Am 1990; 19:319.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
