" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Differential diagnosis of chest pain in adults",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?26/0/26634/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?26/0/26634/contributors\">",
"     James L Meisel, MD, FACP",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?26/0/26634/contributors\">",
"     Daniel Cottrell, MD",
"    </a>",
"    <br/>",
"   </div>",
