" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Inherited aplastic anemia in children",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
