" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Estimated comparative daily doses for inhaled glucocorticoids in children",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\" rowspan=\"2\">",
"        Drug",
"       </td>",
"       <td class=\"subtitle1\" colspan=\"2\">",
