"       <td>",
"        NA",
"       </td>",
"       <td>",
"        &gt;800 mcg",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <p>",
"         Budesonide inhaled:",
"        </p>",
"        <p>",
"         Inhalation suspension for nebulization (child dose)",
"        </p>",
"       </td>",
"       <td>",
"        0.25 to 0.5 mg",
"       </td>",
"       <td>",
"        0.5 mg",
"       </td>",
"       <td>",
"        &gt;0.5 to 1.0 mg",
"       </td>",
"       <td>",
"        1.0 mg",
"       </td>",
"       <td>",
"        &gt;1.0 mg",
"       </td>",
"       <td>",
"        2.0 mg",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <p>",
"         Flunisolide HFA*:",
"        </p>",
"        <p>",
"         80 mcg/puff",
"        </p>",
"       </td>",
"       <td>",
"        NA",
"       </td>",
"       <td>",
"        160 mcg",
"       </td>",
"       <td>",
"        NA",
"       </td>",
