var title_f26_2_26662="Classification of iatrogenic bile duct injury";
var content_f26_2_26662=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F66356&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F66356&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\" style=\"width: 502px\">",
"   <div class=\"ttl\">",
"    Classification of iatrogenic bile duct injury",
"   </div>",
"   <div class=\"cntnt\" style=\"width: 482px; height: 698px; background-image: url(data:image/jpeg;base64,);\">",
"   </div>",
"   <div class=\"lgnd\">",
"    Strasberg classification:",
"    <br>",
"     Type A - Injury&nbsp;to the &nbsp;cystic duct or from minor hepatic ducts draining the liver bed.",
"     <br>",
"      Type B - Occlusion of biliary tree, commonly aberrant right hepatic duct(s)",
"      <br>",
"       Type C -&nbsp;Transection&nbsp;without ligation&nbsp;of aberrant right hepatic duct(s)",
"       <br>",
"        Type D - Lateral injury to a major bile duct.",
"        <br>",
"         Type E (1-5)&nbsp;- Injury to the main hepatic duct; classified according to level of injury (ie, below).&nbsp;",
"         <br>",
"          - E1 (Bismuth type 1) - Injury more than 2 cm from confluence",
"          <br>",
"           - E2 (Bismuth type 2) - Injury less than 2 cm from confluence",
"           <br>",
"            - E3 (Bismuth type 3) - Injury&nbsp;at the confluence; confluence intact",
"            <br>",
"             - E4 (Bismuth type 4) - Destruction of the biliary confluence",
"             <br>",
"              - E5&nbsp;(Bismuth type 5) - Injury to aberrant right hepatic duct",
"              <div class=\"footnotes\">",
"              </div>",
"              <div class=\"reference\">",
"               Strasberg SM, Hertl M, Soper NJ. An analysis of the problem of biliary injury during laparoscopic cholecystectomy. J Am Coll Surg 1995; 180:101.",
"               <br>",
"                Bismuth H, Lazorthes F. Les traumatismes operatoires de la voie biliare principale. Paris, France: Masson, 1981.",
"                <br>",
"                 Chartrand-Lefebvre C, Dufresne M-P, Lafortune M, et al. Iatrogenic injury to the bile duct: A working classification for radiologists. Radiology 1994; 193:523.",
"                </br>",
"               </br>",
"              </div>",
"             </br>",
"            </br>",
"           </br>",
"          </br>",
"         </br>",
"        </br>",
"       </br>",
"      </br>",
"     </br>",
"    </br>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
