var title_f26_20_26955="Presenting symptoms MG";
var content_f26_20_26955=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F70475&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F70475&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Presenting symptoms of myasthenia gravis and the major considerations in the differential diagnosis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Presenting symptoms",
"       </td>",
"       <td class=\"subtitle1\">",
"        Other disorders to consider",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ocular (50 percent)",
"       </td>",
"       <td>",
"        Brainstem and cranial nerve lesions (including Horner's syndrome), thyroid ophthalmopathy, oculopharyngeal muscular dystrophy, chronic external ophthalmoplegia (mitochondrial disease)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Bulbar (15 percent)",
"       </td>",
"       <td>",
"        Brainstem and multiple cranial nerve lesions, motor neuron disease, obstructive or malignant lesion of the nasal and oropharynx",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Limb weakness (&lt;5 percent)",
"       </td>",
"       <td>",
"        Motor neuron disease, chronic inflammatory demyelinating polyneuropathy (CIDP) and other motor neuropathies, multiple radiculopathies, Lambert-Eaton myasthenic syndrome, myopathies",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Isolated neck (uncommon)",
"       </td>",
"       <td>",
"        Motor neuron disease, inflammatory myopathy, paraspinous myopathy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Isolated respiratory (rare)",
"       </td>",
"       <td>",
"        Motor neuron disease, acid maltase deficiency, polymyositis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Distal limb (rare)",
"       </td>",
"       <td>",
"        Motor neuron disease, CIDP and other motor neuropathies, distal myopathies",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
