" <div id=\"printHeader\">",
"  <div id=\"printHeaderLogo\">",
"   <img alt=\"UpToDate\" src=\"./../images/UTD2_masthead.myextg\">",
"    <img align=\"right\" alt=\"Wolters Kluwer Health\" height=\"40\" src=\"./../images/logoWKH.myextg\" width=\"175\">",
