" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Continuous oxygen delivery systems for infants, children, and adults",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?26/24/27014/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?26/24/27014/contributors\">",
"     Pamela Bailey, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?26/24/27014/contributors\">",
"     Section Editors",
